UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第三次會議', '全國司法改革會議籌備委員會第三次會議', 7485, 7484, 7486, '%E5%B0%A4%E4%B8%89%E8%AC%80', '<p>討論事項（四）議決各分組出席人員產生方式之問題，何人參加何組如何決定？是指定、抽籤或自由報名？</p>');
