UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第三次會議', '全國司法改革會議籌備委員會第三次會議', 7487, 7486, 7488, '%E5%B0%A4%E4%B8%89%E8%AC%80', '<p>如一組有八十人報名，有的十數人甚或沒人報名，怎麼辦？</p>');
