UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第五次會議', '全國司法改革會議籌備委員會第五次會議', 7996, 7995, 7997, NULL, '⑷考選部代表：莊慶復。');
