UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第五次會議', '全國司法改革會議籌備委員會第五次會議', 8000, 7999, 8001, NULL, '二、全國司法改革會議正式大會召開一週前舉行說明會，審、檢、辯、學等各界，得於說明會向與會代表說明議題提案背景，不作任何討論與紀錄，說明會之確定日期及地點，授權議事組決定。');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第五次會議', '全國司法改革會議籌備委員會第五次會議', 8001, 8000, 8002, NULL, '三、全國司法改革會議各項議題，歡迎各界提供建言，相關資料可於八十八年七月八日會議結束前寄司法院（司法行政廳）或司法院網站www.judicial.gov.tw電子信箱，由議事組初步整理過濾後，列為會議資料，供與會出席人員參考。');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第五次會議', '全國司法改革會議籌備委員會第五次會議', 8002, 8001, 8003, NULL, '四、與會代表不可跨組參加分組討論，但可跨組提出書面意見。');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第五次會議', '全國司法改革會議籌備委員會第五次會議', 8003, 8002, 8004, NULL, '五、有關民進黨法務政策小組來函提案部分，因其中多項提案與分組議題重覆，不再列入，僅將第七項「檢討現行刑法有關刑期、連續犯之規定」交由法務部處理。');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第五次會議', '全國司法改革會議籌備委員會第五次會議', 8004, 8003, 8005, NULL, '六、「全國司法改革會議議事原則」採發言登記制，以抽籤方式決定發言順序，如登記發言者全部發言完畢，再以舉手方式請求主席發言。請幕僚單位重新草擬草案提下次會議討論。');
