UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8093, 8092, 8094, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>請各位表示意見？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8094, 8093, 8095, '%E6%9E%97%E6%98%8E%E5%BE%B7', '<p>提案二與第二組的議題（第二十頁提案五—㈡以下）重覆，此為當事人進行主義配套措施的一部分，而第二組的議題說明較詳細；且二者若在不同組討論，如有不同的結論，易滋生困擾，是否考慮刪除？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8095, 8094, 8096, '%E9%99%B3%E5%82%B3%E5%B2%B3', '<p>議題重覆在各分組討論，縱有不同結論，仍可提交大會解決爭議；且提案二的討論可宣示本會議重視此問題，意義重大，雖與第二組的議題重覆，仍應保留。本人建議可在第二組的相關議題及提案二後附註說明二者之關聯即可。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8096, 8095, 8097, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>在提案二後附註說明：「最高法院學術研究會相關提案詳見第二組議題。」</p><p>請宣讀下一議題。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8097, 8096, 8098, NULL, '（宣讀提案三、人民對民事事件得否合意選擇法官。）（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8098, 8097, 8099, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>是否保留本案？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8099, 8098, 8100, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>建議保留本案。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8100, 8099, 8101, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>本提案有無立法例？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8101, 8100, 8102, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>本提案並無立法例。</p><p>本提案具體方案的甲案僅係對乙案的反對意見，並非「具體」方案，本人建議將甲案置於背景說明，並明示為司法院的意見；或將具體方案之甲案刪除並保留乙案，再加上本具體方案的利弊說明，將原甲案列為「弊」的說明，至於「利」的部分由本人補充說明。</p><p>學者見解部分，可否將本人名字列入，以加強議題内容。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8102, 8101, 8103, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>人民合意選擇職業法官易生弊端，是否妥適？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8103, 8102, 8104, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>建立人民合意選擇法官的制度為一突破性的主張，可疏解部分司法人力之不足。</p><p>至於可被遴選為非職業法官者，應具備一定資格，此資格由國家規定並造冊，適當人選例如：大學教授、退休法官等。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8104, 8103, 8105, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>職業法官如由人民指定，是否易生弊端，將破壞審判獨立？現制採電腦隨機分案似已可維護審判之獨立。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8105, 8104, 8106, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>職業法官由人民遴選，係經兩造合意，可提升人民的信賴度。如職業法官全由國家指定，人民無信賴基礎。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8106, 8105, 8107, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>以提起確認之訴為例，依法提起確認之訴須有即受確認判決之法律上利益，但若當事人（本無爭執）藉提起確認之訴，意圖以無效判決損害第三人的情形，國家指定的職業法官即可以當事人無即受確認判決之法律上利益將該訴駁回。但如係當事人遴選的法官，則會生弊端致第三人利益受損，危及公共秩序。再加上本案無立法例可循，是否妥適？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8107, 8106, 8108, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>本人仍主張保留本案。本人不反對司法院的反對意見，但將之列在具體方案似有不妥。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8108, 8107, 8109, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>一、仍保留甲案，再將甲、乙兩案次序對調，即將甲案改列乙案，將乙案改列甲案。</p><p>二、背景說明的第三行「應」可酌採……及第四行人民「亦得」……，引號内之文字均改為「是否」。</p><p>三、刪除學者見解。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8109, 8108, 8110, '%E6%9E%97%E5%AD%90%E5%84%80', '<p>建議刪除第二十頁乙案四的部分。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8110, 8109, 8111, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>刪除第二十頁乙案四的部分。請繼續宣讀下一議題。</p><p>（宣讀一—四、民事訴訟制度之改革，提案一、推行民事事件審理集中化，強化第一審之事實審功能，使第一審成為事實審審判中心。）（略）</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8111, 8110, 8112, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>刪除學者見解及執行機關。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8112, 8111, 8113, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>如刪除本提案的執行機關，則應將全部議題中有執行機關的部分全數刪除。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8113, 8112, 8114, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>全部議題中有執行機關的部分全數刪除。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8114, 8113, 8115, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>第二十頁具體方案二的部分，建議增加「提昇法官素質」等文字。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8115, 8114, 8116, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>「提昇法官素質」本來即為持續性及經常性的工作，可否不增加此等文字。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8116, 8115, 8117, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>不堅持。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8117, 8116, 8118, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>原來提案之文字不更動，請繼續宣讀下一提案。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8118, 8117, 8119, NULL, '（宣讀提案二、第二審採事後審或嚴格限制的續審制，第三審採嚴格法律審並採上訴許可制。）（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8119, 8118, 8120, '%E6%9E%97%E6%98%8E%E5%BE%B7', '<p>本案的具體方案為（同提案一），但提案一的具體方案内容空洞，建議應補充具體方案的内容。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8120, 8119, 8121, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>授權議事組補充具體方案的内容。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8121, 8120, 8122, NULL, '（宣讀提案三、除簡易案件之外，第一審應採行合議制。）（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8122, 8121, 8123, '%E6%9E%97%E6%98%8E%E5%BE%B7', '<p>提案二就二、三審程序部份未作整理，具體方案内容同提案一，然於提案一未見相關說明，此部分尚待補充。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8123, 8122, 8124, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>由民事廳會後補增說明。宣讀提案三。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8124, 8123, 8125, NULL, '宣讀提案三。（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8125, 8124, 8126, '%E5%91%A8%E5%8D%A0%E6%98%A5', '<p>第一審採行合議制有無其他配套措施？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8126, 8125, 8127, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>民間團體有無配套措施之方案？立法院就第三十九期司法官分發作成附帶決議，將來第三十九期司法官須先至最高法院或高等法院擔任助理法官，第一審若採合議制必須考量人員數額是否充足及案件多寡等因素。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8127, 8126, 8128, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>背景說明中「訴訟結構採金字塔化」即是配套措施，另具體方案中「……除簡易案件外……」應修訂為「除小額訴訟及簡易訴訟程序外」。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8128, 8127, 8129, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>具體方案之内容於文字起始是否增列「訴訟結構是金字塔型訴訟制度」？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8129, 8128, 8130, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>具體方案之内容應修訂為「配合訴訟結構之金字塔化，民事訴訟法明文規定，除小額訴訟及簡易訴訟程序外，第一審審判應採行合議制。」</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8130, 8129, 8131, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>若無其他意見，即照范委員意見修正之。</p><p>請宣議議題一—五。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8131, 8130, 8132, NULL, '宣議議題一—五。（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8132, 8131, 8133, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>議題一—五全部刪除，法務部有無意見？法務部無意見則議題一—五全部刪除，原議題一—六改為一—五。請宣讀。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8133, 8132, 8134, NULL, '宣讀一—五（原一—六）。（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8134, 8133, 8135, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>評議究應否公開？如同心證形成的過程，世界先進國家似乎無評議公開制度。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8135, 8134, 8136, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>第一、關於提案二具體方案部分，建議將甲案、乙案次序對調；第二、合議制評議之公開在英美國家優於判決書中附不同意見書方式呈現，由此可知已有先例，法院亦可藉由公開評議方式以示對人民負責。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8136, 8135, 8137, '%E9%99%B3%E9%95%B7', '<p>合議制度的落實，除評議是否公開或附不同意見書外，應再探討合議庭的組成，合議庭的組成將來若由資淺的法官參與，實際運作上將不切實際，因為整個合議過程將皆由資深法官主導，欲落實合議制，必須規定具有一定經驗之法官始得參與。其次，背景說明中之準備程序部分，審判長及陪席法官並未參與準備程序，此已違反直接審理、言詞審理的精神，有關證人證詞部分，審判長及陪席法官在準備程序未參與，至言詞辯論時才看卷證，實有違合議制的精神。此部份應在具體方案中強調。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8137, 8136, 8138, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>上述問題將來採行金字塔型訴訟制度時，皆可涵蓋在内。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8138, 8137, 8139, '%E5%91%A8%E5%8D%A0%E6%98%A5', '<p>應告知當事人以下事項，第一、等待訴訟是必須付出的成本，當事人應有此心理準備；第二、訴訟當事人必須支付較高的費用，以新加坡為例，當事人除裁判費外必須另行支付「法庭使用費」，理由在於防止當事人準備不周。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8139, 8138, 8140, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>從人民的觀點，不可以人民必須「等待訴訟」為司法改革條件，此乃政府的責任，司法改革應階段性逐步實施。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8140, 8139, 8141, '%E5%91%A8%E5%8D%A0%E6%98%A5', '<p>我贊成此項提案，但必須告訴當事人會有如此的現象產生。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8141, 8140, 8142, '%E7%9B%A7%E4%BB%81%E7%99%BC', '<p>有關議題内容部分應至分組會議時再作討論。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8142, 8141, 8143, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>具體方案之後段「評議紀錄應於審判後公開之」，「公開」是否意指唯有辯護人可閱覽評議紀錄？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8143, 8142, 8144, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>當事人亦應可閱覽評議紀錄。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8144, 8143, 8145, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>具體方案後段「評議紀錄應於審判後公開之」之「公開」改為「隨時閱覽」。提案二具體方案甲案、乙案次序對調。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8145, 8144, 8146, '%E8%8C%83%E5%85%89%E7%BE%A4', '<p>準備程序本身並未違反直接審理及言詞審理的精神，惟在民事準備程序與刑事調查程序中皆由受命法官「逕行」進行，例如調查證據，才未能貫徹直接審理及言詞審理之精神。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8146, 8145, 8147, '%E5%B0%A4%E4%B8%89%E8%AC%80', '<p>具體方案後段「評議紀錄應於審判後公開之」之「公開」若改為「隨時閱覽」，可能會有問題，蓋聲請閱卷即須經一定之程序，更遑論閱覽評閱簿。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8147, 8146, 8148, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>「隨時閱覽」之「隨時」二字刪除。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8148, 8147, 8149, '%E6%9E%97%E5%AD%90%E5%84%80', '<p>檢察官一—一司法院定位，提案二之乙、丙、丁三案撤回，另提乙案代之，相關資料隨後發給各委員參考。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8149, 8148, 8150, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>請宣讀第二組議題二—一㈠提案一。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8150, 8149, 8151, NULL, '宣讀第二組議題二—一㈠提案一 。（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8151, 8150, 8152, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>甲案「執行機關」部分刪除。對其他文字有無修正意見？無意見，通過。</p><p>請宣讀第二組議題二—一㈠提案二。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8152, 8151, 8153, NULL, '宣讀第二組議題二—一㈠提案二。（略）');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8153, 8152, 8154, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>「執行機關」部分刪除。對其他部分有無修正意見？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8154, 8153, 8155, '%E6%9E%97%E5%AD%90%E5%84%80', '<p>建議背景說明(三)後段「故在無合理期待及信賴保護之情形下，被告較不易認罪或否認罪證到底而一再上訴。」部分，再做適度修正較妥，因從文字上觀之，似乎被告一定要認罪，此與無罪推定有衝突。另外，配套方案二、強化辯護功能部分，是否可配合第一組「提供訴訟救助」部分，做一配套。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8155, 8154, 8156, '%E6%9E%97%E6%98%8E%E5%BE%B7', '<p>為了避免誤會與不了解真意，後段「故在無合理期待及信賴保護之情形下，被告較不易認罪或否認罪證到底而一再上訴。」部分，有其存在之意義，惟如將(三)後段部分刪除亦可，我們可在其他部分再作說明。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8156, 8155, 8157, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>第五頁第五行「故在無合理期待及信賴保護之情形下，被告較不易認罪或否認罪證到底而一再上訴。」部分刪除。又配套措施二、強化辯護功能，是否可涵蓋？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8157, 8156, 8158, '%E6%9E%97%E5%AD%90%E5%84%80', '<p>配套措施宜清楚明確。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8158, 8157, 8159, '%E9%A1%A7%E7%AB%8B%E9%9B%84', '<p>強化辯護功能及法律扶助如還要明確，是否應另建立議題？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8159, 8158, 8160, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>如修改為「強化辯護功能及法律扶助」，各位有何意見？</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8160, 8159, 8161, '%E9%A1%A7%E7%AB%8B%E9%9B%84', '<p>配套5. ⑴「促進當事人實質平等—強化辯護功能」即在講此部分，已全部包含。另外，應否廢除連續犯等與訴因之採行有關，因為採連續犯，故對潛在擴張性部分，應如何解決將有很大之困難，法務部似不反對廢除連續犯之規定，建議增加配套措施四，廢除連續犯之規定。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8161, 8160, 8162, '%E6%A5%8A%E4%BB%81%E5%A3%BD', '<p>是否採行「訴因制度」，係將來由大家決定；惟配套方案應概括涵蓋其内容。</p>');
UPSERT INTO speech_content (filename, nest_filename, nest_display_name, section_id, previous_section_id, next_section_id, section_speaker, section_content) VALUES ('1999年全國司法改革會議', '全國司法改革會議籌備委員會第六次會議', '全國司法改革會議籌備委員會第六次會議', 8162, 8161, NULL, '%E6%9E%97%E6%98%8E%E5%BE%B7', '<p>訴因與實體法有密切關係，增加連續犯、牽連犯之配套措施也是可以，增加配套四：「研討裁判上一罪的範圍」即可。</p>');

